mapper.insert:
INSERT INTO chat_message(
    conversation_id, server_msg_id, client_msg_id, sender_id, from_user_id, msg_type, content, payload_json, hash_code, status, push_attempts, shop_id, created_at, delivered_at
) VALUES (
    #{conversationId}, #{serverMsgId}, #{clientMsgId}, #{senderId}, #{fromUserId}, #{msgType}, #{content}, #{payloadJson}, #{hashCode}, #{status}, #{pushAttempts}, #{shopId}, #{createdAt}, #{deliveredAt}
)

INSERT INTO chat_message(
    conversation_id, server_msg_id, client_msg_id, sender_id, from_user_id, msg_type, content, payload_json, hash_code, status, push_attempts, shop_id, created_at, delivered_at
) VALUES (
    #{conversationId}, #{serverMsgId}, #{clientMsgId}, #{senderId}, #{fromUserId}, #{msgType}, #{content}, #{payloadJson}, #{hashCode}, #{status}, #{pushAttempts}, #{shopId}, #{createdAt}, #{deliveredAt}
)
ON DUPLICATE KEY UPDATE
    server_msg_id = server_msg_id + 1,
    content = VALUES(content),
    payload_json = VALUES(payload_json),
    status = VALUES(status)

mapper.select:
SELECT MAX(server_msg_id)
FROM chat_message
WHERE conversation_id = #{conversationId}

SELECT 
FROM chat_message
WHERE conversation_id = #{conversationId}
  AND client_msg_id = #{clientMsgId}
LIMIT 1
